import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import faker from 'faker'; // Import Faker.js

const BookList = () => {
    const [books, setBooks] = useState([]);

    useEffect(() => {
        fetch('/api/books/')
            .then(response => {
                if (response.ok) {
                    return response.json();
                } else {
                    throw new Error('Failed to fetch books');
                }
            })
            .then(data => {
                setBooks(data);
            })
            .catch(error => {
                console.error('Error:', error);
            });
    }, []);

    // Generate suggested books
    const suggestedBooks = Array.from({ length: 5 }, () => ({
        title: faker.lorem.words(3), // Generate a fake book title
        author: faker.name.findName(), // Generate a fake author name
        id: faker.datatype.uuid(), // Generate a fake ID
    }));

    // Combine existing books with suggested books
    const allBooks = [...books, ...suggestedBooks];

    return (
        <div>
            <h2>Book List</h2>
            <div>
                {allBooks.map((book, index) => (
                    <div key={book.id} style={{ marginLeft: '10px', marginBottom: '10px' }}>
                        <Link to={`/books/${book.id}`}>
                            {book.title} by {book.author}
                        </Link>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default BookList;
